def count_pairs(arr, k, n):
    num_set = set(arr)
    count = 0

    for num in num_set:
        if (num + k) in num_set:
            count += 1
        if (num - k) in num_set:
            count += 1

    # Each pair has been counted twice (once for each element), so divide by 2
    return count // 2

