    str_n = str(n)
    max_sum = 0
    best_split = (0, 0)

    for i in range(1, len(str_n)):
        part1 = int(str_n[:i])
        part2 = int(str_n[i:])
        digit_sum = sum(int(digit) for digit in str(part1)) + sum(int(digit) for digit in str(part2))

