def zigzag(n, k):
    def e(n, k):
        if k == 0 or k == n:
            return 1
        return e(n - 1, k - 1) + e(n - 1, k)

    return e(n, k)

try:
    print(zigzag(4, 3))
except Exception as e:
    print(e)
try:
    print(zigzag(4, 2))
except Exception as e:
    print(e)
