def find_peak(arr, n):
    if n == 1:
        return arr[0]

    if arr[0] >= arr[1]:
        return arr[0]

    if arr[n - 1] >= arr[n - 2]:
        return arr[n - 1]

    for i in range(1, n - 1):
        if arr[i] >= arr[i - 1] and arr[i] >= arr[i + 1]:
            return arr[i]

    return -1  # In case there is no peak, though the problem assumes there's always one.

