def cal_sum(n):
    if n <= 0:
        return 0

    perrin = [3, 0, 2]
    total = sum(perrin)

    for i in range(3, n):
        next_perrin = perrin[i - 2] + perrin[i - 1]
        perrin.append(next_perrin)
        total += next_perrin

    return total

