def max_occurrences(lst):
    from collections import Counter

    if not lst:
        return None

    count = Counter(lst)
    max_count = max(count.values())
    max_items = [key for key, value in count.items() if value == max_count]

    if len(max_items) == 1:
        return max_items[0]
    return tuple(max_items)

