    for row in range(rows):
        for col in range(cols):
            x = col * size * 1.5 + offset_x
            y = row * size * (3 ** 0.5) + offset_y
            if col % 2 == 1:
                y += size * (3 ** 0.5) / 2

            hexagon = [
                (x + size * 0.5, y),
                (x + size * 1.5, y + size * (3 ** 0.5) / 2),
                (x + size * 1.5, y + size * (3 ** 0.5) * 1.5),
                (x + size * 0.5, y + size * (3 ** 0.5) * 2),
                (x, y + size * (3 ** 0.5) * 1.5),
                (x, y + size * (3 ** 0.5) / 2),
            ]
            hexagons.append(hexagon)

    return hexagons

