    missing_ranges = sorted(all_ranges - covered_ranges)
    result = []

    if missing_ranges:
        current_start = missing_ranges[0]
        current_end = missing_ranges[0]

        for i in range(1, len(missing_ranges)):
            if missing_ranges[i] == current_end + 1:
