def get_total_number_of_sequences(n, m):
    if n <= 0 or m <= 0:
        return 0

    dp = [[0] * (m + 1) for _ in range(n)]

    for i in range(1, m + 1):
        dp[0][i] = 1

    for i in range(1, n):
        for j in range(1, m + 1):
            for k in range((j + 1) // 2, j + 1):
                dp[i][j] += dp[i - 1][k]

    return sum(dp[n - 1])

