def prod_Square(n):
    if n < 0:
        return False
    if n == 0:
        return True

    def is_sum_of_two_squares(num):
        for i in range(int(num**0.5) + 1):
            j = (num - i*i)**0.5
            if j.is_integer():
                return True
        return False

    return is_sum_of_two_squares(n)

